<?phpnamespace controllers;use controllers\adminController\DashboardController;use controllers\adminController\DeletePostController;use controllers\adminController\UpdatePostController;use controllers\frontController\AccountController;use controllers\frontController\ConnectionController;use controllers\frontController\DisconnectController;use controllers\frontController\GetPostsController;use controllers\frontController\RegisterController;class AventureController{    private $registerController;    private $connectionController;    private $disconnectController;    private $accountController;    private $dashboardController;    private $getPostsController;    private $deletePostController;    private $updatePostController;    public function __construct()    {        $this->registerController = new RegisterController();        $this->connectionController = new ConnectionController();        $this->disconnectController = new DisconnectController();        $this->accountController = new AccountController();        $this->dashboardController = new DashboardController;        $this->getPostsController = new GetPostsController;        $this->deletePostController = new DeletePostController;        $this->updatePostController = new UpdatePostController;    }    public function run()    {        $op = $_GET['op'] ?? 'home';        switch ($op){            case 'register':                $this->registerController->register();                break;            case 'connect':                $this->connectionController->Connect();                break;            case 'disconnect':                $this->disconnectController->disconnect();                break;            case 'account':                $this->accountController->myAccount();                break;            case 'mon-aventure':                $this->homePage();                break;            case 'qui-suis-je'://                $this->homePage();                break;            case 'administration':                $this->dashboardController->homeDashboard();                break;            case 'post':                $this->getPostsController->getPost($_GET['id']);                break;            case 'deletePost':                $this->deletePostController->deletePost();                break;            case 'updatePost':                $this->updatePostController->updatePost();                break;            default:                $this->homepage();        }    }    public function homePage()    {        $this->getPostsController->getPosts();    }}